package de.terranova.nations.regions.modules.npc;

// Skin bei https://mineskin.org/ hochladen f√ºr signature & texture values.
public enum NPCSkins {
  BEGGAR(
      "",
      "",
      0),
  MONK(
      "",
      "",
      5),
  POOR_LADY_HORNS(
      "",
      "",
      10),
  LADY(
      "",
      "",
      15),
  ;

  private final String SKIN_TEXTURE;
  private final String SKIN_SIGN;

  private final int LEVEL;

  NPCSkins(String skinTexture, String skinSign, int level) {
    this.SKIN_TEXTURE = skinTexture;
    this.SKIN_SIGN = skinSign;
    this.LEVEL = level;
  }

  public String getSkinTexture() {
    return SKIN_TEXTURE;
  }

  public String getSkinSign() {
    return SKIN_SIGN;
  }

  public int getLEVEL() {
    return LEVEL;
  }
}
